'use strict';

var Calculadora = {
    _resultado: 0,
    get resultado() {
        return this._resultado;
    },
    somar: function somar(a) {
        var b = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 0;

        var soma = a + b;
        this._resultado += soma;
        return this;
    },
    potencia: function potencia(a, b) {
        var potencia = Math.pow(a, b);
        this._resultado += potencia;
        return this;
    },
    zerar: function zerar() {
        this._resultado = 0;
        return this;
    },
    log: function log() {
        console.log(this._resultado);
    }
};

fetch('https://myexample.com', {
    method: 'POST',
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
    body: 'foo=bar&blah=1'
}).then(function (response) {
    return response.json();
});
"use strict";

console.log(Calculadora.somar(5).potencia(2, 8).resultado);
Calculadora.zerar().somar(2, 3).somar(3).log();